import React, { useState } from "react";
import axios from "axios";

const FilterForm = ({ uploadedFile }) => {
    const [filterType, setFilterType] = useState("protanopia");
    const [downloadUrl, setDownloadUrl] = useState("");

    const handleFilterChange = (event) => {
        setFilterType(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (uploadedFile) {
            try {
                const response = await axios.post("http://127.0.0.1:8000/process/", {
                    filter_type: filterType,
                    filename: uploadedFile,
                });

                setDownloadUrl(`http://127.0.0.1:8000/${response.data}`);
            } catch (error) {
                alert("Wystąpił błąd podczas przetwarzania obrazu.");
            }
        } else {
            alert("Prześlij plik przed zastosowaniem filtra.");
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    Wybierz filtr:
                    <select value={filterType} onChange={handleFilterChange}>
                        <option value="protanopia">Protanopia</option>
                        <option value="deuteranopia">Deuteranopia</option>
                        <option value="tritanopia">Tritanopia</option>
                    </select>
                </label>
                <button type="submit">Zastosuj filtr</button>
            </form>
            {downloadUrl && (
                <div>
                    <p>Przetworzony obraz:</p>
                    <a href={downloadUrl} download>
                        Pobierz obraz
                    </a>
                </div>
            )}
        </div>
    );
};

export default FilterForm;
