import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import FilterForm from "./components/FilterForm";
import Description from "./components/Description";

const App = () => {
  const [uploadedFile, setUploadedFile] = useState("");

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center py-10">
      <h1 className="text-4xl font-bold mb-8 text-blue-600">
        Aplikacja dla Daltonist√≥w
      </h1>
      <div className="bg-white shadow-lg rounded-lg p-6 w-11/12 max-w-xl">
        <UploadForm setUploadedFile={setUploadedFile} />
        {uploadedFile && (
          <div className="mt-8">
            <FilterForm uploadedFile={uploadedFile} />
          </div>
        )}
      </div>
      <div className="mt-10 w-11/12 max-w-2xl">
        <Description />
      </div>
    </div>
  );
};

export default App;
