import React, { useState } from "react";
import axios from "axios";

const FilterForm = ({ uploadedFile, setPreviewUrl }) => {
  const [filterType, setFilterType] = useState("protanopia");

  const handleFilterChange = (event) => {
    setFilterType(event.target.value);
  };

const handleApplyFilter = async () => {
    console.log("Wysyłam dane do backendu:", {
        filter_type: filterType,
        filename: uploadedFile
    });

    try {
        const response = await axios.post("http://127.0.0.1:8000/process/", {
            filter_type: filterType,
            filename: uploadedFile,
        });
        console.log("Odpowiedź backendu:", response);
        setPreviewUrl(`http://127.0.0.1:8000/${response.data}`);
    } catch (error) {
        console.error("Błąd backendu:", error.response ? error.response.data : error);
        alert("Wystąpił błąd podczas przetwarzania obrazu.");
    }
};

  return (
    <div className="mt-6">
      <label className="text-lg font-medium mr-4">Wybierz filtr:</label>
      <select
        value={filterType}
        onChange={handleFilterChange}
        className="border rounded-lg px-4 py-2"
      >
        <option value="protanopia">Protanopia</option>
        <option value="deuteranopia">Deuteranopia</option>
        <option value="tritanopia">Tritanopia</option>
      </select>
      <button
        onClick={handleApplyFilter}
        className="ml-4 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-500"
      >
        Zastosuj filtr
      </button>
    </div>
  );
};

export default FilterForm;
