import React, { useState } from "react";
import axios from "axios";

const UploadForm = ({ setUploadedFile }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

const handleSubmit = async (event) => {
    event.preventDefault();
    if (file) {
        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post("http://127.0.0.1:8000/upload/", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            console.log("Response from backend:", response.data); // Logowanie odpowiedzi
            setUploadedFile(response.data.filename); // Ustawienie pliku na podstawie odpowiedzi
            alert("Plik został przesłany!");
        } catch (error) {
            console.error("Error uploading file:", error);
            alert("Wystąpił błąd podczas przesyłania pliku.");
        }
    } else {
        alert("Wybierz plik przed przesłaniem.");
    }
};

  return (
    <form onSubmit={handleSubmit} className="flex flex-col items-center">
      <input
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        className="mb-4 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:bg-blue-600 file:text-white"
      />
      <button
        type="submit"
        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-500"
      >
        Prześlij
      </button>
    </form>
  );
};

export default UploadForm;
