import React, { useState } from "react";
import axios from "axios";

const FilterForm = ({ uploadedFile }) => {
  const [filterType, setFilterType] = useState("protanopia");
  const [downloadUrl, setDownloadUrl] = useState("");

  const handleFilterChange = (event) => {
    setFilterType(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (uploadedFile) {
      try {
        const response = await axios.post("http://127.0.0.1:8000/process/", {
          filter_type: filterType,
          filename: uploadedFile,
        });
        setDownloadUrl(`http://127.0.0.1:8000/${response.data}`);
      } catch (error) {
        alert("Wystąpił błąd podczas przetwarzania obrazu.");
      }
    } else {
      alert("Prześlij plik przed zastosowaniem filtra.");
    }
  };

  return (
    <div className="flex flex-col items-center">
      <form onSubmit={handleSubmit} className="flex flex-col items-center">
        <label className="mb-4">
          <span className="text-lg font-medium">Wybierz filtr:</span>
          <select
            value={filterType}
            onChange={handleFilterChange}
            className="ml-2 border rounded-lg px-4 py-2"
          >
            <option value="protanopia">Protanopia</option>
            <option value="deuteranopia">Deuteranopia</option>
            <option value="tritanopia">Tritanopia</option>
          </select>
        </label>
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-500"
        >
          Zastosuj filtr
        </button>
      </form>
      {downloadUrl && (
        <div className="mt-6">
          <p className="text-lg">Przetworzony obraz:</p>
          <a
            href={downloadUrl}
            download
            className="text-blue-600 underline hover:text-blue-800"
          >
            Pobierz obraz
          </a>
        </div>
      )}
    </div>
  );
};

export default FilterForm;
