import React from "react";

const Description = () => {
  return (
    <div className="bg-white shadow-md rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-4">Czym jest daltonizm?</h2>
      <p className="mb-4">
        Daltonizm to wada wzroku polegająca na niezdolności do rozróżniania niektórych kolorów.
      </p>
      <h3 className="text-xl font-semibold">Protanopia</h3>
      <p className="mb-2">Osoby z protanopią nie widzą koloru czerwonego.</p>
      <h3 className="text-xl font-semibold">Deuteranopia</h3>
      <p className="mb-2">Osoby z deuteranopią nie widzą koloru zielonego.</p>
      <h3 className="text-xl font-semibold">Tritanopia</h3>
      <p>Osoby z tritanopią nie widzą koloru niebieskiego.</p>
    </div>
  );
};

export default Description;
