import React, { useState } from "react";
import axios from "axios";

function FilterForm() {
  const [filterType, setFilterType] = useState("protanopia");
  const [downloadUrl, setDownloadUrl] = useState("");

  const applyFilter = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/process/", {
        filename: "uploaded_file.jpg",
        filter_type: filterType,
      });
      setDownloadUrl(response.data.url);
    } catch (error) {
      alert("Błąd podczas przetwarzania obrazu.");
    }
  };

  return (
    <div className="p-4 border rounded shadow">
      <label className="block mb-2 font-bold">Wybierz filtr:</label>
      <select
        className="block w-full mb-4 border rounded"
        value={filterType}
        onChange={(e) => setFilterType(e.target.value)}
      >
        <option value="protanopia">Protanopia</option>
        <option value="deuteranopia">Deuteranopia</option>
        <option value="tritanopia">Tritanopia</option>
      </select>
      <button
        className="bg-green-500 text-white py-2 px-4 rounded"
        onClick={applyFilter}
      >
        Zastosuj filtr
      </button>
      {downloadUrl && (
        <a
          href={downloadUrl}
          download={`filtered_${filterType}.png`}
          className="block mt-4 text-blue-600 underline"
        >
          Pobierz przefiltrowany obraz
        </a>
      )}
    </div>
  );
}

export default FilterForm;
